import React, { useState } from 'react';
import './App.css';

function App() {
  // 用來管理頁簽列表的狀態
  const [tabs, setTabs] = useState([]);
  const [activeTab, setActiveTab] = useState(null);

  // 新增頁簽的函數
  const addTab = () => {
    const newTab = `Tab ${tabs.length + 1}`;
    setTabs([...tabs, newTab]);
    setActiveTab(newTab);  // 預設選中新增的頁簽
  };

  return (
    <div className="container">
      {/* 左側區塊 */}
      <div className="left-panel">
        <div className="info-bar">
          {/* 固定資訊區塊 */}
          <img className="profile-img" src="/headImage.jpg" alt="profile" />
          <span className="username">Account Name</span>
        </div>

        {/* 新增頁簽按鈕 */}
        <button onClick={addTab} className="add-tab-btn">
          新增頁簽
        </button>

        {/* 顯示頁簽的區域 */}
        <div className="tabs">
          {tabs.map((tab, index) => (
            <div
              key={index}
              className={`tab ${tab === activeTab ? 'active' : ''}`}
              onClick={() => setActiveTab(tab)}
            >
              {tab}
            </div>
          ))}
        </div>

        {/* 底部固定區域 */}
        <div className="fixed-bottom">
          <span>Footer</span>
        </div>
      </div>

      {/* 右側區塊 */}
      <div className="right-panel">
        <div className="tab-content">
          {activeTab ? (
            <p>現在顯示的是: {activeTab} 的內容</p>
          ) : (
            <p>請選擇一個頁簽</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
