import React, { useState } from "react";
import './App.css';
import Table from "./Table";  // 引入 Table.js

export default function App() {
  const [activeTab, setActiveTab] = useState("tab1");  // 記錄當前選中的頁簽
  const [tabs, setTabs] = useState([
    { id: "tab1", name: "頁簽 1" },
    { id: "tab2", name: "頁簽 2" },
    { id: "tab3", name: "頁簽 3" }
  ]);

  const [tabContents, setTabContents] = useState({
    tab1: [],
    tab2: [],
    tab3: []
  });

  const [isBlockMenuOpen, setIsBlockMenuOpen] = useState(false);  // 管理選擇區塊的選單顯示狀態

  // 更新頁簽名稱
  const updateTabName = (newName) => {
    setTabs(tabs.map(tab =>
      tab.id === activeTab ? { ...tab, name: newName } : tab
    ));
  };

  // 新增頁簽的函數
  const addTab = () => {
    const newTabId = `tab${tabs.length + 1}`;
    const newTabName = `頁簽 ${tabs.length + 1}`;

    setTabs([...tabs, { id: newTabId, name: newTabName }]);
    setTabContents({
      ...tabContents,
      [newTabId]: []
    });
    setActiveTab(newTabId);  // 預設選中新增的頁簽
  };

  // 新增區塊的函數
  const addBlock = (type) => {
    let newBlock;
    if (type === "text") {
      newBlock = (
        <div key={`text-block-${Date.now()}`} className="block">
          <textarea placeholder="在這裡輸入文字..." className="text-block" />
          <button onClick={() => deleteBlock('text', Date.now())}>刪除區塊</button>  {/* 刪除按鈕 */}
        </div>
      );
    } else if (type === "sheet") {
      newBlock = (
        <div key={`sheet-block-${Date.now()}`} className="block">
          <Table key={`table-block-${Date.now()}`} />  {/* 引入 Table 區塊 */}
          <button onClick={() => deleteBlock('sheet', Date.now())}>刪除區塊</button>  {/* 刪除按鈕 */}
        </div>
      );
    }

    setTabContents(prevContents => ({
      ...prevContents,
      [activeTab]: [...prevContents[activeTab], newBlock]
    }));
    setIsBlockMenuOpen(false);  // 關閉選擇區塊的選單
  };

  // 刪除區塊的函數
  const deleteBlock = (type, key) => {
    setTabContents(prevContents => ({
      ...prevContents,
      [activeTab]: prevContents[activeTab].filter(block => block.key !== key)
    }));
  };

  return (
    <div className="container">
      {/* 左側區塊 */}
      <div className="left-panel">
        {/* 固定在上方的資訊欄 */}
        <div className="info-bar">
          <img src="/headImage.jpg" alt="profile" className="profile-img" />
          <p className="username">帳戶名稱</p>
        </div>

        {/* 顯示可選擇的頁簽 */}
        <div className="tabs">
          {tabs.map((tab) => (
            <div
              key={tab.id}
              className={`tab ${activeTab === tab.id ? "active" : ""}`}
              onClick={() => setActiveTab(tab.id)}  // 點擊頁簽切換
            >
              {tab.name}
            </div>
          ))}
        </div>

        {/* 新增頁簽按鈕 */}
        <button onClick={addTab} className="add-tab-btn">
          新增頁簽
        </button>
      </div>

      {/* 右側區塊 */}
      <div className="right-panel">
        <div className="tab-content">
          {/* 顯示橫幅圖片 */}
          <div className="banner">
            <img src="/bannerImage.jpg" alt="Banner" />
          </div>

          {/* 標題編輯 */}
          <input
            type="text"
            value={tabs.find(tab => tab.id === activeTab)?.name}
            onChange={(e) => updateTabName(e.target.value)}  // 更新頁簽名稱
            className="tab-title-input"
          />

          {/* 顯示頁簽內容 */}
          {tabContents[activeTab]}

          {/* 新增區塊按鈕 */}
          <button onClick={() => setIsBlockMenuOpen(true)} className="add-block-btn">
            + Add new block
          </button>

          {/* 選擇區塊的彈出式選單 */}
          {isBlockMenuOpen && (
            <div className="block-menu">
              <button onClick={() => addBlock("text")}>Text</button>
              <button onClick={() => addBlock("sheet")}>Sheet</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
