import React, { useState } from "react";
import './App.css';

export default function Table() {
  const [columns, setColumns] = useState(["Column 1", "Column 2", "Column 3"]);
  const [rows, setRows] = useState([
    ["Data 1", "Data 2", "Data 3"],
    ["Data 4", "Data 5", "Data 6"]
  ]);

  // 新增欄位
  const addColumn = () => {
    setColumns([...columns, `Column ${columns.length + 1}`]);

    // 為每一行新增一個空單元格
    setRows(rows.map(row => [...row, ""]));
  };

  // 刪除欄位
  const deleteColumn = () => {
    if (columns.length > 1) {
      setColumns(columns.slice(0, -1));

      // 為每一行刪除最後一個單元格
      setRows(rows.map(row => row.slice(0, -1)));
    }
  };

  // 新增行
  const addRow = () => {
    setRows([...rows, Array(columns.length).fill("")]);  // 新行會有與欄數相同的空單元格
  };

  // 刪除行
  const deleteRow = () => {
    if (rows.length > 1) {
      setRows(rows.slice(0, -1));
    }
  };

  // 更新欄標題
  const updateColumnTitle = (index, newTitle) => {
    const updatedColumns = [...columns];
    updatedColumns[index] = newTitle;
    setColumns(updatedColumns);
  };

  // 更新單元格內容
  const updateCell = (rowIndex, colIndex, newValue) => {
    const updatedRows = [...rows];
    updatedRows[rowIndex][colIndex] = newValue;
    setRows(updatedRows);
  };

  return (
    <div className="block">
      <table className="sheet-table">
        <thead>
          <tr>
            {columns.map((col, index) => (
              <th key={index}>
                <input
                  type="text"
                  value={col}
                  onChange={(e) => updateColumnTitle(index, e.target.value)}
                  className="column-title-input"
                />
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rows.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, colIndex) => (
                <td key={colIndex}>
                  <input
                    type="text"
                    value={cell}
                    onChange={(e) => updateCell(rowIndex, colIndex, e.target.value)}
                    className="column-title-input"
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={addColumn}>新增欄位</button>
      <button onClick={deleteColumn}>刪除欄位</button>
      <button onClick={addRow}>新增行</button>
      <button onClick={deleteRow}>刪除行</button>
    </div>
  );
}
