import React, { useState } from "react";
import './App.css';

export default function App() {
  const [activeTab, setActiveTab] = useState("tab1");  // 記錄當前選中的頁簽

  const tabs = [
    { id: "tab1", name: "頁簽 1" },
    { id: "tab2", name: "頁簽 2" },
    { id: "tab3", name: "頁簽 3" }
  ];

  const tabContents = {
    tab1: <div>這是頁簽 1 的內容</div>,
    tab2: <div>這是頁簽 2 的內容</div>,
    tab3: <div>這是頁簽 3 的內容</div>
  };

  return (
    <div className="container">
      {/* 左側區塊 */}
      <div className="left-panel">
        {/* 固定在上方的資訊欄 */}
        <div className="info-bar">
          <img src="/headImage.jpg" alt="profile" className="profile-img" />
          <p className="username">帳戶名稱</p>
        </div>

        {/* 顯示可選擇的頁簽 */}
        <div className="tabs">
          {tabs.map((tab) => (
            <div
              key={tab.id}
              className={`tab ${activeTab === tab.id ? "active" : ""}`}
              onClick={() => setActiveTab(tab.id)}  // 點擊頁簽切換
            >
              {tab.name}
            </div>
          ))}
        </div>
      </div>

      {/* 右側區塊 */}
      <div className="right-panel">
        <div className="tab-content">
          {tabContents[activeTab]}  {/* 顯示選中的頁簽內容 */}
        </div>
      </div>
    </div>
  );
}
