import React, { useState } from "react";
import './App.css';

export default function App() {
  const [activeTab, setActiveTab] = useState("tab1");  // 記錄當前選中的頁簽
  const [tabs, setTabs] = useState([
    { id: "tab1", name: "頁簽 1" },
    { id: "tab2", name: "頁簽 2" },
    { id: "tab3", name: "頁簽 3" }
  ]);

  const [tabContents, setTabContents] = useState({
    tab1: <div>這是頁簽 1 的內容</div>,
    tab2: <div>這是頁簽 2 的內容</div>,
    tab3: <div>這是頁簽 3 的內容</div>
  });

  const [tabTitles, setTabTitles] = useState({
    tab1: "頁簽 1",
    tab2: "頁簽 2",
    tab3: "頁簽 3"
  });

  // 新增頁簽的函數
  const addTab = () => {
    const newTabId = `tab${tabs.length + 1}`;
    const newTabName = `頁簽 ${tabs.length + 1}`;

    setTabs([...tabs, { id: newTabId, name: newTabName }]);
    setTabContents({
      ...tabContents,
      [newTabId]: <div>這是 {newTabName} 的內容</div>
    });
    setTabTitles({
      ...tabTitles,
      [newTabId]: newTabName
    });
    setActiveTab(newTabId);  // 預設選中新增的頁簽
  };

  // 處理標題變更
  const handleTitleChange = (e) => {
    const newTitle = e.target.value;
    setTabTitles({
      ...tabTitles,
      [activeTab]: newTitle
    });
  };

  return (
    <div className="container">
      {/* 左側區塊 */}
      <div className="left-panel">
        {/* 固定在上方的資訊欄 */}
        <div className="info-bar">
          <img src="/headImage.jpg" alt="profile" className="profile-img" />
          <p className="username">帳戶名稱</p>
        </div>

        {/* 顯示可選擇的頁簽 */}
        <div className="tabs">
          {tabs.map((tab) => (
            <div
              key={tab.id}
              className={`tab ${activeTab === tab.id ? "active" : ""}`}
              onClick={() => setActiveTab(tab.id)}  // 點擊頁簽切換
            >
              {tab.name}
            </div>
          ))}
        </div>

        {/* 新增頁簽按鈕 */}
        <button onClick={addTab} className="add-tab-btn">
          新增頁簽
        </button>
      </div>

      {/* 右側區塊 */}
      <div className="right-panel">
        <div className="tab-content">
          {/* 顯示選中的頁簽內容 */}
          <div>
            <input
              type="text"
              value={tabTitles[activeTab]}
              onChange={handleTitleChange}
              className="tab-title-input"
            />
            <div>{tabContents[activeTab]}</div>
          </div>
        </div>
      </div>
    </div>
  );
}
